package pcr_test

import (
	"crypto/rsa"
	"crypto/x509"
	"encoding/pem"
	"os"
	"testing"

	"github.com/google/go-tpm/tpm2"
	"github.com/stretchr/testify/require"

	"github.com/aenix-io/talm/internal/pkg/secureboot"
	"github.com/aenix-io/talm/internal/pkg/secureboot/measure/internal/pcr"
	tpm2internal "github.com/aenix-io/talm/internal/pkg/secureboot/tpm2"
)

type keyWrapper struct {
	*rsa.PrivateKey
}

func (k keyWrapper) PublicRSAKey() *rsa.PublicKey {
	return &k.PrivateKey.PublicKey
}

func TestCalculateBankData(t *testing.T) {
	t.Parallel()

	pemKey, err := os.ReadFile("../../testdata/pcr-signing-key.pem")
	require.NoError(t, err)

	block, _ := pem.Decode(pemKey)
	require.NotNil(t, block)

	key, err := x509.ParsePKCS1PrivateKey(block.Bytes)
	require.NoError(t, err)

	bankData, err := pcr.CalculateBankData(15, tpm2.TPMAlgSHA256,
		map[secureboot.Section]string{
			secureboot.Initrd: "testdata/a",
			secureboot.Linux:  "testdata/b",
			secureboot.DTB:    "testdata/c",
		},
		keyWrapper{key})
	require.NoError(t, err)

	require.Equal(t,
		[]tpm2internal.BankData{
			{
				PCRs: []int{15},
				PKFP: "58f58f625bd8a8b6681e4b40688cf99b26419b6b2c5f6e14a2c7c67a3b0b1620",
				Pol:  "a1c9d366451c82969238eb82a5282f84b6a3d499e540430ecf792083155225dd",
				Sig:  "", //nolint:lll
			},
		}, bankData)
}
